function Paging(url,Result_Deal_FuncName,object){this.url=url;this.object=object;this.Result_Deal=Result_Deal_FuncName;this.num_per_page=10;this.totalRecordNum=0;this.cur_page=1;this.sendReq=sendReq;this.go_last_page=go_last_page;this.go_next_page=go_next_page;this.go_first_page=go_first_page}
function sendReq(page_num){var self=this;self.cur_page=page_num;var Result_Deal=this.Result_Deal;if(this.url.indexOf("?")==-1)reqURL=this.url+"?pageSize="+this.num_per_page+"&page="+page_num;else reqURL=this.url+"&pageSize="+this.num_per_page+"&page="+page_num;jQuery.ajax({url:reqURL,dataType:"jsonp",beforeSend:function(){},success:function(data,textStatus){var myjson=data;self.totalRecordNum=myjson.total=="undefined"?self.totalRecordNum:myjson.total;Result_Deal.call(self.object,myjson)},complete:function(){},
error:function(e){alert("\u67e5\u8be2CMS\u9875\u9762\u5931\u8d25\uff01")}})}function go_last_page(){if(this.cur_page>1)this.sendReq(this.cur_page-1);else alert("\u5f53\u524d\u9875\u5df2\u4e3a\u7b2c\u4e00\u9875")}function go_next_page(){if(this.cur_page<=parseInt(this.totalRecordNum/this.num_per_page))this.sendReq(this.cur_page+1);else alert("\u5f53\u524d\u9875\u5df2\u4e3a\u6700\u540e\u4e00\u9875")}function go_first_page(){this.sendReq(1)};
